    private void butMgrsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butMgrsActionPerformed
       
        
      String mgrs = Coordinates.mgrsFromLatLon(37.10, -112.12);
       
      //System.out.println(mgrs);
      mgrs = txtMgrs.getText().toString();
      
      double[] latLon = Coordinates.latLonFromMgrs(mgrs);
      
      txtLat.setText(String.format("%.6f", latLon[0]));
      txtLong.setText(String.format("%.6f", latLon[1]));
      //System.out.println(
       // String.format("%s, %s", latLon[0], latLon[1]));
    }//GEN-LAST:event_butMgrsActionPerformed

