    private void butGeoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butGeoActionPerformed
        
        String mgrs = Coordinates.mgrsFromLatLon(Double.parseDouble(txtLat.getText()), Double.parseDouble(txtLong.getText()));
        txtMgrs.setText(mgrs);
        
    }//GEN-LAST:event_butGeoActionPerformed

